"use strict";

(function executeRule(cur) {
  var teamGr = new GlideRecord('x_nuvo_csd_field_service_team');
  teamGr.addQuery('name', cur.getValue('name'));
  teamGr.query();

  if (teamGr.next()) {
    cur.setAbortAction(true);
    gs.addErrorMessage(gs.getMessage('CSD_DISP_DUPLICATE_TEAM'));
  }
})(current);